Configuration:
{
       ClusterKit {
        NodeManager.ConfigurationDatabaseConnectionString = "User ID=postgres;Host=configDb;Port=5432;Pooling=true"
NodeManager.DefaultNugetFeeds {
              publicFeed {
                      address = "/opt/packageCache"
                      type = public
              } 
              privateFeed {
                      address = "http://192.168.99.100:81/"
                      type = private
              }          
          }  
        Web {
          Swagger.Publish {
              publishDocPath = "clusterkit/manager/swagger/doc"
              publishUiPath = "clusterkit/manager/swagger/ui"
          }
          Services {
            ClusterKit/Web/Swagger { // ServiceName is just unique service identification, used in order to handle stacked config properly. It is used just localy on node
              Port = 8080 // default owin port, current node listening port for server access
              PublicHostName = default //public host name of this service. It doesn't supposed (but is not prohibited) that this should be real public service hostname. It's just used to distinguish services with identical url paths to be correctly published on frontend web servers. Real expected hostname should be configured in NginxConfigurator or similar publisher
              Route = /clusterkit/manager/swagger //route (aka directory) path to service
            }
          }
        }
      }
    }
